<?php/** * Description of User * * @author Suharshana DsW * @web www.nysc.lk */class Booking {    public $id;    public $name;    public $address;    public $dob;    public $email;    public $contact;    public $whatsapp;    public $grama_wasama;    public $grama_no;    public $location;    public function __construct($id) {        if ($id) {            $query = "SELECT `id`,`name`,`address`,`dob`,`email`,`contact`,`whatsapp`,`grama_wasama`,`grama_no`,`location` FROM `suhuru_bookings` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->name = $result['name'];            $this->address = $result['address'];            $this->dob = $result['dob'];            $this->email = $result['email'];            $this->contact = $result['contact'];            $this->whatsapp = $result['whatsapp'];            $this->grama_wasama = $result['grama_wasama'];            $this->grama_no = $result['grama_no'];            $this->location = $result['location'];            return $result;        }    }    public function create() {        $query = "INSERT INTO `suhuru_bookings` (`name`,`address`,`dob`,`email`,`contact`,`whatsapp`,`grama_wasama`,`grama_no`,`location`) VALUES  ('"                . $this->name . "','"                . $this->address . "', '"                . $this->dob . "', '"                . $this->email . "', '"                . $this->contact . "', '"                . $this->whatsapp . "', '"                . $this->grama_wasama . "', '"                . $this->grama_no . "', '"                . $this->location . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return TRUE;        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `suhuru_bookings`";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `suhuru_bookings` SET "                . "`name` ='" . $this->name . "', "                . "`address` ='" . $this->address . "', "                . "`dob` ='" . $this->dob . "', "                . "`email` ='" . $this->email . "', "                . "`contact` ='" . $this->contact . "', "                . "`whatsapp` ='" . $this->whatsapp . "', "                . "`grama_wasama` ='" . $this->grama_wasama . "', "                . "`grama_no` ='" . $this->grama_no . "', "                . "`location` ='" . $this->location . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $query = 'DELETE FROM `suhuru_bookings` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }}